module.exports = function(app) {
  // Module dependencies.
  var mongoose = require('mongoose'),
      Todo = mongoose.models.Todo,
      api = {};

  // ALL
  api.todos = function (req, res) {
    Todo.find(function(err, todos) {
      if (err) {
        res.json(500, err);
      } else {    
        res.json({todos: todos});
      }
    });
  };

  // GET
  api.todo = function (req, res) {
    var id = req.params.id;
    Todo.findOne({ '_id': id }, function(err, todo) {
      if (err) {
        res.json(404, err);
      } else {
        res.json({todo: todo});
      }
    });
  };

  // POST
  api.addTodo = function (req, res) {
    
    var todo;
      
    if(typeof req.body.todo == 'undefined'){
         res.status(500);
         return res.json({message: 'todo is undefined'});
    }

    todo = new Todo(req.body.todo);

    todo.save(function (err) {
      if (!err) {
        console.log("created todo");
        return res.json(201, todo.toObject());
      } else {
        return res.json(500, err);
      }
    });

  };

  // PUT
  api.editTodo = function (req, res) {
    var id = req.params.id;

    Todo.findById(id, function (err, todo) {


    
      if(typeof req.body.todo["complete"] != 'undefined'){
        todo["complete"] = req.body.todo["complete"];
      }  
    
      if(typeof req.body.todo["created"] != 'undefined'){
        todo["created"] = req.body.todo["created"];
      }  
    
      if(typeof req.body.todo["task"] != 'undefined'){
        todo["task"] = req.body.todo["task"];
      }  
    

      return todo.save(function (err) {
        if (!err) {
          console.log("updated todo");
          return res.json(200, todo.toObject());        
        } else {
         return res.json(500, err);
        }
        return res.json(todo);
      });
    });

  };

  // DELETE
  api.deleteTodo = function (req, res) {
    var id = req.params.id;
    return Todo.findById(id, function (err, todo) {
      return todo.remove(function (err) {
        if (!err) {
          console.log("removed todo");
          return res.send(204);
        } else {
          console.log(err);
          return res.json(500, err);
        }
      });
    });

  };


  app.get('/api/todos', api.todos);
  app.get('/api/todo/:id', api.todo);
  app.post('/api/todo', api.addTodo);
  app.put('/api/todo/:id', api.editTodo);
  app.delete('/api/todo/:id', api.deleteTodo);
};